import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button, Input } from 'semantic-ui-react'




//https://api.openaq.org/v1/cities


function App() {
  const [cityData, setCityData] = useState([]);
  const [cities, setCities] = useState([]);
  const [city1, setCity1] = useState("");
  const [city2, setCity2] = useState("");

  // useEffect(() => {
  //   const countryURL = 'https://api.openaq.org/v1/cities';

  //   fetch(countryURL)
  //     .then(response => response.json()).then(data => { console.log(data) });

  // });
  function getCitiesList() {
    const countryURL = 'https://api.openaq.org/v1/cities';
    console.log(city1);
    fetch(countryURL)
      .then(response => response.json()).then(data => { getCityNames(data) });
  }

  function getCityNames(data) {
    let cityNames = [];
    data.results.forEach(item => cityNames = [...cityNames, item.name]);
    console.log(cityNames);
    setCities(cityNames);
    setCityData(data);
  }

  return (
    <div className="App">
      <header className="App-header">

        <Input onChange={e => setCity1(e)}></Input>
        <Input style={{ padding: 10 }}></Input>
        <Button onClick={getCitiesList}>Enter</Button>
        <div >test</div>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          <div></div>
        </a>
      </header>
    </div>
  );
}

export default App;
