import logo from './logo.svg';
import './App.css';


//https://api.openaq.org/v1/cities
this.state = {
  // Populates the dropdown box ("CountriesList" component)
  countriesList: [],
  // When a country is selected, contains the last measurement ("MeasurementDetails" component)
  measurementData: null,
}
function getCitiesList() {
  const countryURL = 'https://api.openaq.org/v1/countries';

  fetch(countryURL)
    .then(response => response.json())
    .then(data => {
      this.setState({ countriesList: data.results });
    });
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>{getCitiesList()}</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
