import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button, Input, Table } from 'semantic-ui-react'
import { TableExamplePagination } from './TableExamplePagination'




//https://api.openaq.org/v1/cities


function App() {
  const [cityData, setCityData] = useState([]);
  const [cities, setCities] = useState([]);
  const [city1, setCity1] = useState("");
  const [city2, setCity2] = useState("");
  const [city1data, setCity1Data] = useState([]);

  // useEffect(() => {
  //   const countryURL = 'https://api.openaq.org/v1/cities';

  //   fetch(countryURL)
  //     .then(response => response.json()).then(data => { console.log(data) });

  // });

  const options = {
    method: 'GET',
    body: JSON.stringify(city1)
  };
  function getCitiesList() {
    const countryURL = 'https://api.openaq.org/v2/locations/';
    fetch(countryURL + city1)
      .then(response => response.json()).then(data => { setCity1Data(data) });
    if (city1data.length != 0 && city1data.results.length != 0) {
      console.log(city1data.results[0].parameters)
    }
  }


  function getCityNames(data) {
    let cityNames = [];
    data.results.forEach(item => cityNames = [...cityNames, item.name]);
    console.log(cityNames);
    setCities(cityNames);
    setCityData(data);
  }

  return (
    <div className="App">
      <header className="App-header">

        <Input placeholder={"Enter a city code"} onChange={e => setCity1(e.target.value)}></Input>
        <Input placeholder={"Enter a city code"} style={{ padding: 10 }} onChange={e => setCity2(e.target.value)}></Input>
        <Button onClick={getCitiesList}>{"Enter"}</Button>
        <TableExamplePagination></TableExamplePagination>

        <div style={{ padding: 10 }}>{city1}</div>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          <div></div>
        </a>
      </header>
    </div>
  );
}

export default App;
