import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button } from 'semantic-ui-react'




//https://api.openaq.org/v1/cities


function App() {
  const [cityData, setCityData] = useState([]);
  const [cities, setCities] = useState([]);

  function getCitiesList() {
    const countryURL = 'https://api.openaq.org/v1/cities';

    fetch(countryURL)
      .then(response => response.json()).then(data => { setCityData(data.results) });
    getCityNames();
  }

  function getCityNames() {
    let cityNames = [];
    cityData.forEach(item => cityNames = [...cityNames, item.name]);
    setCities(cityNames);
    console.log(cities);
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <button onClick={getCitiesList}>test</button>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
